from django.db import models
from django import forms
from slugify import slugify
from django.utils.safestring import mark_safe
from django.db.models import Count
no_photo_base64 = "data:image/png;base64,"


def get_html_img(picture):
    return mark_safe(f'<img src="{picture.url if picture else no_photo_base64}" style="max-height: 50px;">')


class Point(models.Model):
    name = models.CharField(max_length=200, verbose_name="Имя точки")
    address = models.CharField(max_length=500, verbose_name="Адрес точки")
    volume = models.IntegerField(default=100, verbose_name="рубли")
    picture = models.ImageField(upload_to="img/points", blank=True, null=True, verbose_name="картинка")
    subnetwork = models.CharField(max_length=10, default="")
    ordering = models.IntegerField('ordering', default=0)

    def __str__(self):
        return self.name

    class Meta:
        ordering = ('ordering', )


class Image(models.Model):
    title = models.CharField(max_length=200, verbose_name="Имя картинки", default='')
    slug = models.CharField(max_length=200, verbose_name="slug картинки", default='')
    picture = models.ImageField(upload_to="img/main", blank=True, null=True, verbose_name="картинка")
    mobile = models.BooleanField('mobile', default=False)
    ordering = models.IntegerField('ordering', default=0)

    def __str__(self):
        if self.title:
            return self.title + ' ' + str(self.pk)
        else:
            return str(self.pk)

    def preview(self):
        return get_html_img(self.picture)

    @property
    def rank(self):
        current = Data.current()
        n = 1
        for img in current.menu_pictures.filter(mobile=self.mobile).order_by('ordering'):
            if img.pk == self.pk:
                return n
            n += 1

    def save(self, force_insert=False, force_update=False, using=None, update_fields=None):
        self.slug = slugify(self.slug)
        super().save()


class Data(models.Model):
    menu = models.FileField(upload_to="datas", blank=True, null=True, verbose_name="Меню")
    menu_pictures = models.ManyToManyField(Image, verbose_name="картинки для меню")
    active = models.BooleanField('active', default=True)

    def save(self, force_insert=False, force_update=False, using=None, update_fields=None):
        if self.active:
            type(self).objects.exclude(pk=self.pk).update(active=False)
        super().save()

    @staticmethod
    def current():
        return Data.objects.filter(active=True).last()





